import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = props => {
  const [selectedYear, setSelectedYear] = useState('');

  const yearChangeHandler = option => {
    console.log(option);
    // props.onPickedYear(option)
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={yearChangeHandler(2022)}>
            2022
          </option>
          <option value="2021" onChange={yearChangeHandler(2021)}>
            2021
          </option>
          <option value="2020" onChange={yearChangeHandler(2020)}>
            2020
          </option>
          <option value="2019" onChange={yearChangeHandler(2019)}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
